import React, {useState, useEffect, useRef} from 'react';
import styles from '../styles/HeroCarousel.module.scss';

const HeroCarousel = () => {
    const [currentSlide, setCurrentSlide] = useState(0);
    const [isLoaded, setIsLoaded] = useState(false);
    const slidesRef = useRef([]);

    const slides = [
        {
            id: 1,
            image: 'https://i.ibb.co/ZKQHK96/Cricket-World-Cup-Banner.jpg',
            alt: 'Cricket World Cup Banner',
        },
        {
            id: 2,
            image: 'https://i.ibb.co/KhFZ7z5/Football-Champions-League-Banner.jpg',
            alt: 'Champions League Football Banner',
        },
        {
            id: 3,
            image: 'https://i.ibb.co/JtcS3Fq/NBA-Finals-Banner.jpg',
            alt: 'NBA Finals Banner',
        },
    ];

    useEffect(() => {
        // Wait for images to load before starting the carousel
        Promise.all(
            slides.map(slide => {
                return new Promise((resolve) => {
                    const img = new Image();
                    img.onload = resolve;
                    img.src = slide.image;
                });
            })
        ).then(() => {
            setIsLoaded(true);
        });

        const interval = setInterval(() => {
            if (isLoaded) {
                setCurrentSlide((prevSlide) =>
                    prevSlide === slides.length - 1 ? 0 : prevSlide + 1
                );
            }
        }, 5000);

        return () => clearInterval(interval);
    }, [slides.length, isLoaded]);

    const goToSlide = (index) => {
        setCurrentSlide(index);
    };

    return (
        <section className={styles.hero}>
            <div className={styles.slider}>
                {slides.map((slide, index) => (
                    <div
                        key={slide.id}
                        ref={el => slidesRef.current[index] = el}
                        className={`${styles.slide} ${index === currentSlide ? styles.active : ''}`}
                    >
                        <img src={slide.image} alt={slide.alt}/>
                        <div className={styles.slideOverlay}>
                            <div className={styles.slideContent}>
                                <h2>{slide.alt}</h2>
                                <a href="#" className={styles.readMoreBtn}>Read More</a>
                            </div>
                        </div>
                    </div>
                ))}
                <div className={styles.slideIndicator}>
                    {slides.map((slide, index) => (
                        <span
                            key={slide.id}
                            className={index === currentSlide ? styles.active : ''}
                            onClick={() => goToSlide(index)}
                        ></span>
                    ))}
                </div>
            </div>
        </section>
    );
};

export default HeroCarousel;
